package chap10_11.composition;import chap05.Money;import chap10_11.Call;import java.time.Duration;public class NightlyDiscountPolicy extends BaseRatePolicy {	private static final int LATE_NIGHT_HOUR = 22;	private Money nightlyAmount;	private Money regularAmount;	private Duration seconds;	public NightlyDiscountPolicy(Money nightlyAmount, Money regularAmount, Duration seconds) {		this.nightlyAmount = nightlyAmount;		this.regularAmount = regularAmount;		this.seconds = seconds;	}	@Override protected Money calculateFee(Call call) {		if (call.getFrom().getHour() >= LATE_NIGHT_HOUR) {			return nightlyAmount.times(call.getDuration().getSeconds() / seconds.getSeconds());		}		return regularAmount.times(call.getDuration().getSeconds() / seconds.getSeconds());	}}