package chap10_11.composition;import chap05.Money;import chap10_11.Call;import java.util.ArrayList;import java.util.List;public class Phone {	private RatePolicy ratePolicy;	private List<Call> calls = new ArrayList<>();	public void call(Call call) {		calls.add(call);	}	public List<Call> getCalls() {		return calls;	}	public Phone(RatePolicy ratePolicy) {		this.ratePolicy = ratePolicy;	}	public void setRatePolicy(RatePolicy ratePolicy) {		this.ratePolicy = ratePolicy;	}	public Money calculateFee() {		return ratePolicy.calculateFee(this);	}}