package chap10_11.composition;import chap05.Money;import chap10_11.Call;import java.time.Duration;public class RegularPolicy extends BaseRatePolicy {	private Money amount;	private Duration seconds;	public RegularPolicy(Money amount, Duration seconds) {		this.amount = amount;		this.seconds = seconds;	}	@Override protected Money calculateFee(Call call) {		return amount.times(call.getDuration().getSeconds() / seconds.getSeconds());	}}