import java.time.Durationimport chap02.Moneyimport chap10_11.{Call, Phone}abstract class BasicRatePolicy {  def calculateFee(phone: Phone): Money = Money.wons(1)  //phone.calls.map(calculateFee(_)).reduce(_ + _)  protected def calculateFee(call: Call): Money;}class RegularPolicy(val amount: Money, val seconds: Duration) extends BasicRatePolicy {  override protected def calculateFee(call: Call): Money = amount * (call.getDuration.getSeconds / seconds.getSeconds)}class NightlyDiscountPolicy(val nighlyAmount: Money,                            val regularAmount: Money,                            val seconds: Duration) extends BasicRatePolicy {  override protected def calculateFee(call: Call): Money = Money.ZERO  // business logic}object NightlyDiscountPolicy {  val LateNightHour : Integer = 22}