package chap10_11import chap05.Moneyimport spock.lang.Specificationimport java.time.Durationimport java.time.LocalDateTimeclass PhoneSpecification extends Specification {    def "일반 요금"() {        def phone = new Phone(Money.wons(5), Duration.ofSeconds(10))        phone.call(new Call(LocalDateTime.of(2018, 1, 1, 12, 10, 0),                LocalDateTime.of(2018, 1, 1, 12, 11, 0)))        phone.call(new Call(LocalDateTime.of(2018, 2, 1, 12, 10, 0),                LocalDateTime.of(2018, 2, 1, 12, 11, 0)))        when:        def amount = phone.calculateFee()        then:        amount.isEqualsAmount(Money.wons(60))    }}