package chap10_11.compositionimport chap05.Moneyimport spock.lang.Specificationimport java.time.Durationclass PhoneSpecification extends  Specification{    def "합성을 이용한 phone"(){        def phone = new Phone(new RegularPolicy(Money.wons(10), Duration.ofSeconds(10)))        def additionalPolicyPhone = new Phone(new TaxablePolicy(new RegularPolicy(Money.wons(10), Duration.ofSeconds(10)), Money.wons(5)));        def taxableMoney = additionalPolicyPhone.calculateFee();        expect:        phone.calculateFee().isGreaterThanOrEquals(taxableMoney)    }}