package chap12import spock.lang.Specificationclass LectureSpecification extends Specification {    def "수강 점수를 계산한다."(){        def lecture = new Lecture(50, "OOP", [60,50,49,30])        expect:        lecture.evaluate() == "Pass:2 Fail:2"    }    def "수강 점수를 통계 내린다."(){        def lecture = new GradleLecture(70,"OOP", [60,50,49,30], [new Grade("A",70,60),new Grade("B",60,50), new Grade("C",50,30)])        print lecture.evaluate()        expect:        lecture.evaluate() == "Pass:0 Fail:4 , A:1B:2C:3"    }}