package chap01import spock.lang.Specificationclass TicketOfficeSpecification extends Specification {    def "theater 입장 test"() {        given:        def audience = new Audience(new Bag(0, new Invitation()))        def ticketOffice = Mock(TicketOffice)        def seller = Mock(TicketSeller)        def theater = new Theater(seller)        when:        theater.enter(audience)        then:        seller.getTicketOffice() >> ticketOffice        ticketOffice.getTicket() >> new Ticket()        audience.getBag()    }}