package chap01import spock.lang.Specificationclass TicketOfficeSpecification extends Specification {    def "test"() {        given:        def audience = Mock(Audience)        def seller = Mock(TicketSeller)        def theater = new Theater(seller)        when:        // mocking 확인        theater.enter(audience)        then:        audience.getBag() >> new Bag(0, new Invitation())        seller.getTicketOffice().getTicket() >> new Ticket()    }}